<?phpget_header();$transient_post_id = get_the_ID();$opts = get_post_meta($transient_post_id, 'form_opts', true);$area_meta = advisory_area_exists($transient_post_id, advisory_id_from_string($_GET['area']));$area_id = advisory_id_from_string($_GET['area']);if (isset($_GET['view']) && $_GET['view'] == 'true') {    $select_attr = 'disabled';    $publish_btn = true;    $prefix = 'view=true&';} elseif (isset($_GET['edit']) && $_GET['edit'] == 'true') {    $select_attr = '';    $publish_btn = false;    $prefix = 'edit=true&';} else {    $select_attr = '';    $publish_btn = true;    $prefix = '';} ?>    <div class="content-wrapper">        <!-- Page Title -->        <div class="page-title">            <div>                <h1><?php echo (!empty($area_meta['icon_title']) ? '<img src="' . $area_meta['icon_title'] . '"> ' : '') ?><?php echo $area_meta['name'] ?></h1>            </div>            <?php if ($select_attr == '') { ?>                <div>                    <?php if($publish_btn == true) {                        if (advisory_is_valid_form_submission($transient_post_id)) {                            echo '<a class="btn btn-lg btn-info btn-publish is-bia" href="#" data-id="' . $transient_post_id . '">Publish</a>';                        } else {                            echo '<a class="btn btn-lg btn-default btn-publish is-bia" href="#" data-id="' . $transient_post_id . '">Publish</a>';                        }                    } ?>                    <a class="btn btn-lg btn-success btn-save-all" href="#">Save All</a>                    <a class="btn btn-lg btn-warning btn-reset-all" href="#">Reset</a>                </div>            <?php } ?>            <div>                <ul class="breadcrumb">                    <li><i class="fa fa-home fa-lg"></i></li>                    <li><a href="#"><?php echo advisory_get_form_name($transient_post_id) ?></a></li>                    <li><a href="#"><?php echo $area_meta['name'] ?></a></li>                </ul>            </div>        </div>        <div class="card">            <div class="card-body">                <div class="row">                    <div class="col-sm-7">                        <img src="<?php echo get_template_directory_uri()?>/images/project_risk_process.png" class="img-responsive">                    </div>                </div>            </div>        </div>        <?php if ($opts['areas']) {            foreach ($opts['areas'] as $threat_cat) {                if (@$_GET['area'] != advisory_id_from_string($threat_cat['name'])) {                    continue;                }                $cat_id = advisory_id_from_string($threat_cat['name']);                $default = advisory_form_default_values($transient_post_id, $cat_id);                echo '<form class="form survey-form" method="post" data-meta="' . $cat_id . '" data-id="'. $transient_post_id .'">                    <div class="row">                        <div class="col-md-12">                            <div class="card">                                <div class="card-title-w-btn">                                    <h4 class="title">Threat Category: ' . $threat_cat['name'] . '</h4>';                                    if ($select_attr == '') {                                        echo '<button class="btn btn-success btn-submit-primary" type="submit"><i class="fa fa-lg fa-floppy-o"></i> Save</button>';                                    }                                echo '</div>                                <div class="card-body">                                    <textarea name="comment" id="comment" class="form-control" cols="30" rows="10" ' . $select_attr . '>' . @$default['comment'] . '</textarea>                                    <input type="hidden" name="reset" value="true">                                </div>                            </div>                        </div>                    </div>                </form>';                if ($threats = $opts[advisory_id_from_string($threat_cat['name']) . '_threats']) {                    foreach ($threats as $threat) {                        $threat_id = $cat_id . '_' . advisory_id_from_string($threat['name']);                        $default = advisory_form_default_values($transient_post_id, $threat_id);                        $avgBtn = dynamicRegisterAvgCalculation($threat['avg']);                        echo '<form class="form survey-form bia-risk" method="post" data-meta="' . $threat_id . '" data-id="'. $transient_post_id .'">                            <div class="row">                                <div class="col-md-12">                                    <div class="card">                                        <div class="card-title-w-btn">                                            <h4 class="title">Threat: ' . @$threat['name'] . '</h4>';                                            if ($select_attr == '') {                                                echo '<button class="btn btn-success" type="submit"><i class="fa fa-lg fa-floppy-o"></i> Save</button>';                                            }                                        echo '</div>                                        <div class="card-body">                                            <div class="row">                                                <div class="col-sm-8">                                                    <p class="text-left strong font-110p">' . @$threat['desc'] . '</p>                                                </div>                                                <div class="col-sm-2">'. $avgBtn .'</div>                                                <div class="col-sm-2">                                                    <div class="total-risk-avg text-center font-120p"><strong>Avg: <span>'. $threat['avg'] .'</span></strong></div>                                                </div>                                                <br><br>                                            </div>                                        </div>                                        <div class="card-footer text-right">';                                            if ($select_attr == '') {                                                echo '<input type="hidden" name="avg" class="hidden-avg" value="0"><button class="btn btn-success btn-submit-primary" type="submit"><i class="fa fa-lg fa-floppy-o"></i> Save</button>';                                            }                                        echo '</div>                                    </div>                                </div>                            </div>                        </form>';                    }                }            }        }    echo '</div>';get_footer(); ?>